+        if (password.length() == 0)
+            return false;
+        if (android.util.Patterns.EMAIL_ADDRESS.matcher(email).matches() == false)
+            return false;
+        if (Patterns.PHONE.matcher(contactNumber).matches() == false)
+            return false;
+        return true;
