+  text = text.map(function (str) {
+    str = String(str)
+    width = stringWidth(str)
+    maxWidth = Math.max(width, maxWidth)
+    return {
+      str,
+      width
+    }
+  }).map(function (obj) {
+    return new Array(widthDiffFn(maxWidth, obj.width) + 1).join(pad) + obj.str
+  })
+
+  return returnString ? text.join(split) : text
